import React, { useState } from 'react';
import './SubjectForm.css';

const SubjectForm = ({ onSubmit, onCancel }) => {
    const [formData, setFormData] = useState({
        name: '',
        code: '',
        description: '',
        credits: '',
        schedule: '',
        maxStudents: ''
    });

    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
        // Limpiar error cuando el usuario modifica el campo
        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};
        
        if (!formData.name.trim()) {
            newErrors.name = 'El nombre es requerido';
        }
        
        if (!formData.code.trim()) {
            newErrors.code = 'El código es requerido';
        } else if (!/^[A-Z0-9]{6}$/.test(formData.code)) {
            newErrors.code = 'El código debe tener 6 caracteres alfanuméricos';
        }
        
        if (!formData.description.trim()) {
            newErrors.description = 'La descripción es requerida';
        }
        
        if (!formData.credits) {
            newErrors.credits = 'Los créditos son requeridos';
        } else if (isNaN(formData.credits) || formData.credits < 1 || formData.credits > 12) {
            newErrors.credits = 'Los créditos deben ser entre 1 y 12';
        }
        
        if (!formData.schedule.trim()) {
            newErrors.schedule = 'El horario es requerido';
        }
        
        if (!formData.maxStudents) {
            newErrors.maxStudents = 'El cupo máximo es requerido';
        } else if (isNaN(formData.maxStudents) || formData.maxStudents < 1) {
            newErrors.maxStudents = 'El cupo máximo debe ser mayor a 0';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validateForm()) {
            onSubmit(formData);
        }
    };

    return (
        <div className="subject-form-container">
            <form onSubmit={handleSubmit} className="subject-form">
                <h2>Registrar Nueva Asignatura</h2>
                
                <div className="form-group">
                    <label htmlFor="name">Nombre de la Asignatura</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        className={errors.name ? 'error' : ''}
                        placeholder="Ej: Matemáticas Avanzadas"
                    />
                    {errors.name && <span className="error-message">{errors.name}</span>}
                </div>

                <div className="form-group">
                    <label htmlFor="code">Código</label>
                    <input
                        type="text"
                        id="code"
                        name="code"
                        value={formData.code}
                        onChange={handleChange}
                        className={errors.code ? 'error' : ''}
                        placeholder="Ej: MAT101"
                    />
                    {errors.code && <span className="error-message">{errors.code}</span>}
                </div>

                <div className="form-group">
                    <label htmlFor="description">Descripción</label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        className={errors.description ? 'error' : ''}
                        placeholder="Describe el contenido y objetivos de la asignatura"
                        rows="4"
                    />
                    {errors.description && <span className="error-message">{errors.description}</span>}
                </div>

                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="credits">Créditos</label>
                        <input
                            type="number"
                            id="credits"
                            name="credits"
                            value={formData.credits}
                            onChange={handleChange}
                            className={errors.credits ? 'error' : ''}
                            min="1"
                            max="12"
                        />
                        {errors.credits && <span className="error-message">{errors.credits}</span>}
                    </div>

                    <div className="form-group">
                        <label htmlFor="maxStudents">Cupo Máximo</label>
                        <input
                            type="number"
                            id="maxStudents"
                            name="maxStudents"
                            value={formData.maxStudents}
                            onChange={handleChange}
                            className={errors.maxStudents ? 'error' : ''}
                            min="1"
                        />
                        {errors.maxStudents && <span className="error-message">{errors.maxStudents}</span>}
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="schedule">Horario</label>
                    <input
                        type="text"
                        id="schedule"
                        name="schedule"
                        value={formData.schedule}
                        onChange={handleChange}
                        className={errors.schedule ? 'error' : ''}
                        placeholder="Ej: Lunes y Miércoles 10:00-12:00"
                    />
                    {errors.schedule && <span className="error-message">{errors.schedule}</span>}
                </div>

                <div className="form-actions">
                    <button type="button" className="cancel-button" onClick={onCancel}>
                        Cancelar
                    </button>
                    <button type="submit" className="submit-button">
                        Registrar Asignatura
                    </button>
                </div>
            </form>
        </div>
    );
};

export default SubjectForm;
