import React, { useState, useEffect } from 'react';
import { subjectService } from '../../services/subject.service';
import SubjectForm from './SubjectForm';
import Notification from '../common/Notification';
import './Subjects.css';

const Subjects = ({ professorId }) => {
    const [subjects, setSubjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showForm, setShowForm] = useState(false);
    const [notification, setNotification] = useState(null);

    useEffect(() => {
        loadSubjects();
    }, [professorId]);

    const loadSubjects = async () => {
        try {
            setLoading(true);
            const data = await subjectService.getSubjects(professorId);
            setSubjects(data);
            setError(null);
        } catch (err) {
            setError('Error al cargar las asignaturas');
            console.error('Error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleCreateSubject = async (subjectData) => {
        try {
            const newSubject = await subjectService.createSubject({
                ...subjectData,
                professor_id: professorId
            });
            setSubjects(prev => [newSubject, ...prev]);
            setShowForm(false);
            setNotification({
                type: 'success',
                message: 'Asignatura registrada con éxito'
            });
        } catch (err) {
            setNotification({
                type: 'error',
                message: 'Error al registrar la asignatura'
            });
            console.error('Error:', err);
        }
    };

    const handleDeleteSubject = async (subjectId) => {
        if (window.confirm('¿Estás seguro de que deseas eliminar esta asignatura?')) {
            try {
                await subjectService.deleteSubject(subjectId);
                setSubjects(prev => prev.filter(subject => subject.id !== subjectId));
                setNotification({
                    type: 'success',
                    message: 'Asignatura eliminada con éxito'
                });
            } catch (err) {
                setNotification({
                    type: 'error',
                    message: 'Error al eliminar la asignatura'
                });
                console.error('Error:', err);
            }
        }
    };

    if (loading) {
        return <div className="loading">Cargando asignaturas...</div>;
    }

    return (
        <div className="subjects-container">
            {notification && (
                <Notification
                    type={notification.type}
                    message={notification.message}
                    onClose={() => setNotification(null)}
                />
            )}

            <div className="subjects-header">
                <h2>Mis Asignaturas</h2>
                <button 
                    className="add-subject-button"
                    onClick={() => setShowForm(true)}
                >
                    + Nueva Asignatura
                </button>
            </div>

            {showForm && (
                <SubjectForm
                    onSubmit={handleCreateSubject}
                    onCancel={() => setShowForm(false)}
                />
            )}

            {error && <div className="error-message">{error}</div>}

            {subjects.length === 0 ? (
                <div className="no-subjects">
                    <p>No tienes asignaturas registradas.</p>
                    <button 
                        className="add-subject-button"
                        onClick={() => setShowForm(true)}
                    >
                        Registrar Primera Asignatura
                    </button>
                </div>
            ) : (
                <div className="subjects-grid">
                    {subjects.map(subject => (
                        <div key={subject.id} className="subject-card">
                            <div className="subject-header">
                                <h3>{subject.name}</h3>
                                <span className="subject-code">{subject.code}</span>
                            </div>
                            <p className="subject-description">{subject.description}</p>
                            <div className="subject-details">
                                <div className="detail-item">
                                    <span className="detail-label">Créditos:</span>
                                    <span className="detail-value">{subject.credits}</span>
                                </div>
                                <div className="detail-item">
                                    <span className="detail-label">Horario:</span>
                                    <span className="detail-value">{subject.schedule}</span>
                                </div>
                                <div className="detail-item">
                                    <span className="detail-label">Cupo:</span>
                                    <span className="detail-value">{subject.maxStudents}</span>
                                </div>
                            </div>
                            <div className="subject-actions">
                                <button 
                                    className="delete-button"
                                    onClick={() => handleDeleteSubject(subject.id)}
                                >
                                    Eliminar
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Subjects;
