import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../config';
import { deepseekService } from '../../services/deepseek.service';
import Subjects from './Subjects';
import './Dashboard.css';

const Dashboard = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('subjects');
    const [chatMessage, setChatMessage] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [isProcessing, setIsProcessing] = useState(false);

    useEffect(() => {
        const loadUser = async () => {
            try {
                const { data: { user } } = await supabase.auth.getUser();
                if (!user) {
                    navigate('/login');
                    return;
                }

                const { data: profile } = await supabase
                    .from('profiles')
                    .select('*')
                    .eq('id', user.id)
                    .single();

                setUser({ ...user, ...profile });
            } catch (error) {
                console.error('Error al cargar el usuario:', error);
                navigate('/login');
            } finally {
                setLoading(false);
            }
        };

        loadUser();
    }, [navigate]);

    const handleLogout = async () => {
        try {
            await supabase.auth.signOut();
            navigate('/login');
        } catch (error) {
            console.error('Error al cerrar sesiÃ³n:', error);
        }
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!chatMessage.trim() || isProcessing) return;

        const userMessage = chatMessage.trim();
        setChatMessage('');
        setChatHistory(prev => [...prev, { role: 'user', content: userMessage }]);
        setIsProcessing(true);

        try {
            const response = await deepseekService.sendMessage(userMessage);
            setChatHistory(prev => [...prev, { role: 'assistant', content: response }]);
        } catch (error) {
            console.error('Error al enviar mensaje:', error);
            setChatHistory(prev => [...prev, { 
                role: 'assistant', 
                content: 'Lo siento, ha ocurrido un error al procesar tu mensaje. Por favor, intenta de nuevo.' 
            }]);
        } finally {
            setIsProcessing(false);
        }
    };

    if (loading) {
        return <div className="loading">Cargando...</div>;
    }

    return (
        <div className="dashboard">
            <nav className="dashboard-nav">
                <div className="nav-brand">
                    <h1>EDUIA</h1>
                </div>
                <div className="nav-user">
                    <span>Bienvenido, {user?.name || 'Profesor'}</span>
                    <button onClick={handleLogout} className="logout-button">
                        Cerrar SesiÃ³n
                    </button>
                </div>
            </nav>

            <div className="dashboard-content">
                <div className="dashboard-sidebar">
                    <button 
                        className={`sidebar-button ${activeTab === 'subjects' ? 'active' : ''}`}
                        onClick={() => setActiveTab('subjects')}
                    >
                        Mis Asignaturas
                    </button>
                    <button 
                        className={`sidebar-button ${activeTab === 'chat' ? 'active' : ''}`}
                        onClick={() => setActiveTab('chat')}
                    >
                        Asistente IA
                    </button>
                    <button 
                        className={`sidebar-button ${activeTab === 'settings' ? 'active' : ''}`}
                        onClick={() => setActiveTab('settings')}
                    >
                        ConfiguraciÃ³n
                    </button>
                </div>

                <div className="dashboard-main">
                    {activeTab === 'subjects' && <Subjects />}
                    
                    {activeTab === 'chat' && (
                        <div className="chat-container">
                            <div className="chat-messages">
                                {chatHistory.length === 0 ? (
                                    <div className="welcome-message">
                                        <h2>Bienvenido al Asistente IA</h2>
                                        <p>Puedo ayudarte con:</p>
                                        <ul>
                                            <li>DiseÃ±o de actividades educativas</li>
                                            <li>GeneraciÃ³n de preguntas y ejercicios</li>
                                            <li>AnÃ¡lisis de respuestas de estudiantes</li>
                                            <li>Sugerencias pedagÃ³gicas</li>
                                            <li>Recursos educativos</li>
                                        </ul>
                                    </div>
                                ) : (
                                    chatHistory.map((message, index) => (
                                        <div 
                                            key={index} 
                                            className={`message ${message.role === 'user' ? 'user-message' : 'assistant-message'}`}
                                        >
                                            {message.content}
                                        </div>
                                    ))
                                )}
                                {isProcessing && (
                                    <div className="message assistant-message processing">
                                        <div className="typing-indicator">
                                            <span></span>
                                            <span></span>
                                            <span></span>
                                        </div>
                                    </div>
                                )}
                            </div>
                            <form onSubmit={handleSendMessage} className="chat-input">
                                <input
                                    type="text"
                                    value={chatMessage}
                                    onChange={(e) => setChatMessage(e.target.value)}
                                    placeholder="Escribe tu mensaje..."
                                    disabled={isProcessing}
                                />
                                <button type="submit" disabled={isProcessing || !chatMessage.trim()}>
                                    Enviar
                                </button>
                            </form>
                        </div>
                    )}

                    {activeTab === 'settings' && (
                        <div className="settings-container">
                            <h2>ConfiguraciÃ³n</h2>
                            <div className="settings-form">
                                <div className="form-group">
                                    <label>Nombre Completo</label>
                                    <input 
                                        type="text" 
                                        defaultValue={user?.name || ''} 
                                        readOnly 
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Correo ElectrÃ³nico</label>
                                    <input 
                                        type="email" 
                                        defaultValue={user?.email || ''} 
                                        readOnly 
                                    />
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
