import { config } from '../config';

class DeepSeekService {
    constructor() {
        this.apiUrl = 'https://api.deepseek.com/v1/chat/completions';
        this.apiKey = process.env.REACT_APP_DEEPSEEK_API_KEY;
        
        // Debug: Mostrar todas las variables de entorno
        console.log('=== DEBUG: Variables de entorno ===');
        console.log('REACT_APP_DEEPSEEK_API_KEY:', this.apiKey ? 'Configurada' : 'No configurada');
        console.log('NODE_ENV:', process.env.NODE_ENV);
        console.log('Todas las variables REACT_APP_:', 
            Object.keys(process.env)
                .filter(key => key.startsWith('REACT_APP_'))
                .map(key => `${key}: ${process.env[key] ? 'Configurada' : 'No configurada'}`)
        );
        console.log('================================');
    }

    async sendMessage(message) {
        if (!this.apiKey) {
            console.error('Error: La API key de DeepSeek no está configurada.');
            console.error('Valor actual de REACT_APP_DEEPSEEK_API_KEY:', this.apiKey);
            throw new Error('Por favor, configura la API key de DeepSeek en el archivo .env');
        }

        try {
            console.log('Enviando mensaje a DeepSeek...');
            const response = await fetch(this.apiUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${this.apiKey}`
                },
                body: JSON.stringify({
                    messages: [{
                        role: 'user',
                        content: message
                    }],
                    model: 'deepseek-chat',
                    temperature: 0.7,
                    max_tokens: 1000
                })
            });

            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                console.error('Error en la respuesta de DeepSeek:', errorData);
                throw new Error(`Error en la API: ${response.status} - ${errorData.error?.message || 'Error desconocido'}`);
            }

            const data = await response.json();
            console.log('Respuesta recibida de DeepSeek:', data);
            return data.choices[0].message.content;
        } catch (error) {
            console.error('Error al enviar mensaje a DeepSeek:', error);
            throw new Error('No se pudo obtener una respuesta del asistente. Por favor, intenta de nuevo.');
        }
    }
}

export const deepseekService = new DeepSeekService();
