import React, { useState, useEffect } from 'react';
import './SubjectForm.css';
import SyllabusUpload from './SyllabusUpload';

const SubjectForm = ({ onSubmit, onCancel, initialData }) => {
    const [formData, setFormData] = useState({
        name: ''
    });
    const [error, setError] = useState(null);
    const [syllabusUrl, setSyllabusUrl] = useState(null);
    const [subjectId, setSubjectId] = useState(null);
    const [isSubjectCreated, setIsSubjectCreated] = useState(false);

    useEffect(() => {
        if (initialData) {
            setFormData({
                name: initialData.name || ''
            });
            setSubjectId(initialData.id);
            setIsSubjectCreated(true);
        }
    }, [initialData]);

    const generateCode = (name) => {
        const words = name.split(' ');
        const code = words
            .map(word => word.substring(0, 3).toUpperCase())
            .join('');
        return code;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        if (!formData.name.trim()) {
            setError('Por favor, ingrese el nombre de la asignatura');
            return;
        }

        try {
            const code = generateCode(formData.name);
            const result = await onSubmit({
                ...formData,
                code
            });
            
            if (result && result.id && !initialData) {
                setSubjectId(result.id);
                setIsSubjectCreated(true);
            }
        } catch (error) {
            setError(error.message);
        }
    };

    const handleSyllabusUpload = (url) => {
        setSyllabusUrl(url);
    };

    const handleNewSubject = () => {
        setIsSubjectCreated(false);
        setFormData({ name: '' });
        setSubjectId(null);
        setSyllabusUrl(null);
    };

    return (
        <div className="subject-form-container">
            <form onSubmit={handleSubmit} className="subject-form">
                <div className="form-section">
                    <h3>{initialData ? 'Editar Asignatura' : 'InformaciÃ³n de la Asignatura'}</h3>
                    <div className="form-group">
                        <label htmlFor="name">Nombre de la Asignatura</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            placeholder="Ej: MatemÃ¡ticas Avanzadas"
                            required
                            disabled={isSubjectCreated && !initialData}
                        />
                        <small className="form-text">
                            El cÃ³digo se generarÃ¡ automÃ¡ticamente basado en el nombre
                        </small>
                    </div>
                </div>

                {error && <div className="error-message">{error}</div>}

                {isSubjectCreated && (
                    <div className="form-section">
                        <div className="success-message">
                            <h3>Â¡Asignatura {initialData ? 'actualizada' : 'creada'} exitosamente!</h3>
                            <p>Ahora puedes subir el sÃ­labo de la asignatura.</p>
                        </div>
                        <div className="syllabus-upload-section">
                            <h4>Carga del SÃ­labo</h4>
                            <SyllabusUpload
                                subjectId={subjectId}
                                onUploadComplete={handleSyllabusUpload}
                            />
                        </div>
                    </div>
                )}

                <div className="form-actions">
                    {isSubjectCreated && !initialData ? (
                        <button 
                            type="button" 
                            className="cancel-button" 
                            onClick={handleNewSubject}
                        >
                            Crear otra asignatura
                        </button>
                    ) : (
                        <>
                            <button type="button" className="cancel-button" onClick={onCancel}>
                                Cancelar
                            </button>
                            <button type="submit" className="submit-button">
                                {initialData ? 'Actualizar Asignatura' : 'Crear Asignatura'}
                            </button>
                        </>
                    )}
                </div>
            </form>
        </div>
    );
};

export default SubjectForm;
