import React, { useState } from 'react';
import { supabase } from '../../config/supabaseClient';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/esm/Page/AnnotationLayer.css';
import 'react-pdf/dist/esm/Page/TextLayer.css';
import './PDFUpload.css';

// Configurar el worker de PDF.js
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const PDFUpload = ({ subjectId }) => {
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(false);
    const [uploadedFileUrl, setUploadedFileUrl] = useState(null);
    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile && selectedFile.type === 'application/pdf') {
            setFile(selectedFile);
            setError(null);
            // Crear URL temporal para previsualización
            const fileUrl = URL.createObjectURL(selectedFile);
            setUploadedFileUrl(fileUrl);
        } else {
            setError('Por favor, selecciona un archivo PDF válido');
            setFile(null);
            setUploadedFileUrl(null);
        }
    };

    const handleUpload = async () => {
        if (!file) {
            setError('Por favor, selecciona un archivo PDF');
            return;
        }

        setUploading(true);
        setError(null);
        setSuccess(false);

        try {
            const fileExt = file.name.split('.').pop();
            const fileName = `${Math.random().toString(36).substring(2)}_${Date.now()}.${fileExt}`;
            const filePath = `pdfs/${fileName}`;

            // Subir archivo al storage
            const { error: uploadError } = await supabase.storage
                .from('documents')
                .upload(filePath, file, {
                    cacheControl: '3600',
                    upsert: false
                });

            if (uploadError) throw uploadError;

            // Obtener URL pública
            const { data: { publicUrl } } = supabase.storage
                .from('documents')
                .getPublicUrl(filePath);

            // Guardar en la base de datos
            const { error: dbError } = await supabase
                .from('files')
                .insert([
                    {
                        subject_id: subjectId,
                        file_name: file.name,
                        file_url: publicUrl,
                        file_type: 'application/pdf',
                        file_size: file.size,
                        folder: 'pdfs'
                    }
                ]);

            if (dbError) throw dbError;

            setSuccess(true);
            setFile(null);
            setUploadedFileUrl(publicUrl);
            // Limpiar el input file
            document.getElementById('pdf-upload').value = '';
        } catch (error) {
            console.error('Error al subir PDF:', error);
            setError('Error al subir el archivo PDF');
        } finally {
            setUploading(false);
        }
    };

    const onDocumentLoadSuccess = ({ numPages }) => {
        setNumPages(numPages);
        setPageNumber(1);
    };

    const changePage = (offset) => {
        setPageNumber(prevPageNumber => {
            const newPageNumber = prevPageNumber + offset;
            return Math.min(Math.max(1, newPageNumber), numPages);
        });
    };

    const previousPage = () => changePage(-1);
    const nextPage = () => changePage(1);

    return (
        <div className="pdf-upload-container">
            <h3>Subir Documento PDF</h3>
            
            <div className="upload-section">
                <input
                    type="file"
                    id="pdf-upload"
                    accept=".pdf"
                    onChange={handleFileChange}
                    className="file-input"
                />
                <label htmlFor="pdf-upload" className="file-label">
                    {file ? file.name : 'Seleccionar PDF'}
                </label>
            </div>

            {error && <div className="error-message">{error}</div>}
            {success && <div className="success-message">PDF subido exitosamente</div>}

            <button
                onClick={handleUpload}
                disabled={!file || uploading}
                className="upload-button"
            >
                {uploading ? 'Subiendo...' : 'Subir PDF'}
            </button>

            {uploadedFileUrl && (
                <div className="pdf-viewer">
                    <h4>Vista Previa del PDF</h4>
                    <div className="pdf-controls">
                        <button
                            onClick={previousPage}
                            disabled={pageNumber <= 1}
                            className="page-button"
                        >
                            Anterior
                        </button>
                        <span className="page-info">
                            Página {pageNumber} de {numPages}
                        </span>
                        <button
                            onClick={nextPage}
                            disabled={pageNumber >= numPages}
                            className="page-button"
                        >
                            Siguiente
                        </button>
                    </div>
                    <div className="pdf-document">
                        <Document
                            file={uploadedFileUrl}
                            onLoadSuccess={onDocumentLoadSuccess}
                            loading={<div className="loading">Cargando PDF...</div>}
                            error={<div className="error-message">Error al cargar el PDF</div>}
                        >
                            <Page
                                pageNumber={pageNumber}
                                renderTextLayer={false}
                                renderAnnotationLayer={false}
                                scale={1.2}
                            />
                        </Document>
                    </div>
                </div>
            )}
        </div>
    );
};

export default PDFUpload;
