import { supabase } from '../config';

class SubjectService {
    async getSubjects(professorId) {
        try {
            const { data, error } = await supabase
                .from('subjects')
                .select('*')
                .eq('professor_id', professorId)
                .order('created_at', { ascending: false });

            if (error) throw error;
            return data;
        } catch (error) {
            console.error('Error al obtener asignaturas:', error);
            throw error;
        }
    }

    async createSubject(subjectData) {
        try {
            // Validar datos requeridos
            if (!subjectData.name || !subjectData.professor_id) {
                throw new Error('Faltan datos requeridos');
            }

            // Generar código automáticamente
            const code = this.generateCode(subjectData.name);

            // Verificar usuario autenticado
            const { data: { user }, error: authError } = await supabase.auth.getUser();
            if (authError) throw authError;

            if (!user) {
                throw new Error('Usuario no autenticado');
            }

            // Verificar que el usuario autenticado coincida con el professor_id
            if (user.id !== subjectData.professor_id) {
                throw new Error('No tienes permiso para crear asignaturas para otros profesores');
            }

            // Verificar si el usuario existe en nuestra tabla users
            const { data: existingUser, error: userError } = await supabase
                .from('users')
                .select('id')
                .eq('id', user.id)
                .single();

            if (userError || !existingUser) {
                // Si el usuario no existe en nuestra tabla, lo creamos
                const { error: insertError } = await supabase
                    .from('users')
                    .insert([
                        {
                            id: user.id,
                            email: user.email,
                            full_name: user.user_metadata?.full_name || user.email,
                            role: 'professor'
                        }
                    ]);

                if (insertError) {
                    console.error('Error al crear usuario:', insertError);
                    throw new Error('Error al sincronizar el usuario');
                }
            }

            // Intentar crear la asignatura
            const { data, error } = await supabase
                .from('subjects')
                .insert([
                    {
                        name: subjectData.name,
                        code: code,
                        professor_id: subjectData.professor_id
                    }
                ])
                .select()
                .single();

            if (error) {
                console.error('Error al crear asignatura:', error);
                if (error.code === '42501') {
                    throw new Error('No tienes permiso para crear asignaturas');
                }
                throw error;
            }

            return data;
        } catch (error) {
            console.error('Error en createSubject:', error);
            throw error;
        }
    }

    generateCode(name) {
        // Tomar las primeras tres letras de cada palabra y convertirlas a mayúsculas
        return name
            .split(' ')
            .map(word => word.substring(0, 3).toUpperCase())
            .join('');
    }

    async deleteSubject(subjectId, professorId) {
        try {
            // Verificar usuario autenticado
            const { data: { user }, error: authError } = await supabase.auth.getUser();
            if (authError) throw authError;

            if (!user) {
                throw new Error('Usuario no autenticado');
            }

            // Verificar que el usuario autenticado coincida con el professor_id
            if (user.id !== professorId) {
                throw new Error('No tienes permiso para eliminar asignaturas de otros profesores');
            }

            const { error } = await supabase
                .from('subjects')
                .delete()
                .eq('id', subjectId)
                .eq('professor_id', professorId);

            if (error) {
                console.error('Error al eliminar asignatura:', error);
                if (error.code === '42501') {
                    throw new Error('No tienes permiso para eliminar asignaturas');
                }
                throw error;
            }

            return true;
        } catch (error) {
            console.error('Error en deleteSubject:', error);
            throw error;
        }
    }

    async getSubjectById(subjectId) {
        try {
            if (!subjectId) {
                throw new Error('ID de la asignatura no proporcionado');
            }

            const { data, error } = await supabase
                .from('subjects')
                .select('*')
                .eq('id', subjectId)
                .single();

            if (error) {
                console.error('Error de Supabase:', error);
                throw error;
            }

            return data;
        } catch (error) {
            console.error('Error al obtener la asignatura:', error);
            throw error;
        }
    }

    async updateSubject(subjectId, subjectData) {
        try {
            if (!subjectId) {
                throw new Error('ID de la asignatura no proporcionado');
            }

            const { data, error } = await supabase
                .from('subjects')
                .update({
                    name: subjectData.name,
                    description: subjectData.description,
                    code: subjectData.code
                })
                .eq('id', subjectId)
                .select();

            if (error) {
                console.error('Error de Supabase:', error);
                throw error;
            }

            return data[0];
        } catch (error) {
            console.error('Error al actualizar la asignatura:', error);
            throw error;
        }
    }

    async getEnrolledStudents(subjectId) {
        try {
            const { data, error } = await supabase
                .from('enrollments')
                .select(`
                    student_id,
                    students:profiles (
                        id,
                        full_name,
                        email
                    )
                `)
                .eq('subject_id', subjectId);

            if (error) throw error;
            return data.map(enrollment => enrollment.students);
        } catch (error) {
            console.error('Error al obtener los estudiantes inscritos:', error);
            throw error;
        }
    }
}

export const subjectService = new SubjectService();
